import React, { useEffect } from 'react';
import './SplashScreen.css';

const SplashScreen = ({ onSplashComplete }) => {
  useEffect(() => {
    // Auto-hide splash screen after 5 seconds
    const timer = setTimeout(() => {
      onSplashComplete();
    }, 5000);

    return () => clearTimeout(timer);
  }, [onSplashComplete]);

  return (
    <div className="splash-screen">
      {/* Background Image */}
      <div className="splash-background-image"></div>
      
      {/* Overlay for better text readability */}
      <div className="splash-overlay"></div>
      
      {/* Scrolling Text Container */}
      <div className="scrolling-text-container">
        <div className="scrolling-text">
          Share your feedback • Help improve courses • Your voice matters • Enhance learning experience • Make a difference • Shape better education • Connect with educators • Build better tomorrow • Share your feedback • Help improve courses • Your voice matters • Enhance learning experience • Make a difference • Shape better education • Connect with educators • Build better tomorrow •
        </div>
      </div>

      {/* Additional scrolling lines */}
      <div className="scrolling-text-container line-2">
        <div className="scrolling-text reverse">
          Student Feedback System • BIWA2110 Web Development • Limkokwing University • Faculty of ICT • Quality Education • Student Engagement • Academic Excellence • Continuous Improvement • Student Feedback System • BIWA2110 Web Development • Limkokwing University • Faculty of ICT • Quality Education • Student Engagement • Academic Excellence • Continuous Improvement •
        </div>
      </div>

      <div className="scrolling-text-container line-3">
        <div className="scrolling-text">
          Your Opinion Counts • Improve Teaching Methods • Better Learning Outcomes • Share Your Experience • Rate Your Courses • Anonymous Feedback • Constructive Criticism • Positive Reinforcement • Your Opinion Counts • Improve Teaching Methods • Better Learning Outcomes • Share Your Experience • Rate Your Courses • Anonymous Feedback • Constructive Criticism • Positive Reinforcement •
        </div>
      </div>

      {/* Countdown Timer */}
      <div className="countdown-timer">
        <div className="countdown-circle">
          <span className="countdown-text">5</span>
        </div>
        <p>Loading your feedback portal...</p>
      </div>
    </div>
  );
};

export default SplashScreen;
